输出杨辉三角
# include&lt;iostream&gt;using namespace std;int main()&#123;    int a =0,c=1,b=0;    cin&gt;&gt;b;    while(a&lt;b)    &#123;        for (int i = c; i &lt; b; i++)		&#123;			cout &lt;&lt; &quot; &quot;;		&#125;		c++;		static int s = 1;		for (int j = 0; j &lt;2*s-1; j++)		&#123;			cout &lt;&lt; &quot;*&quot;;		&#125;		cout &lt;&lt; endl;		s++;		a++;    &#125;    return 0;&#125;



给出指定范围，找出其中的素数
# include&lt;iostream&gt;using namespace std;int main()&#123;    int a = 0, b = 0, d = 0;	cin &gt;&gt; a &gt;&gt; b;	for ( a +=1; a &lt; b; a++)	&#123;		for (int c = 2; c &lt; a; c++)		&#123;			if (a%c==0)			&#123;				d=1;				continue;			&#125;		&#125;		if (d == 0)	cout &lt;&lt; a &lt;&lt; endl;		else d = 0;	&#125;    return 0;&#125;

用1，2，3，4组合成没有相同数字的三位数
