我的编程练习
输出杨辉三角
# include&lt;iostream&gt;using namespace std;int main()&#123;    int c = 1, b = 0;	cin &gt;&gt; b;	for (int a = 0; a &lt; b; a++)	&#123;		for (int i = c; i &lt; b; i++)		&#123;			cout &lt;&lt; &quot; &quot;;		&#125;		c++;		static int s = 1;		for (int j = 0; j &lt; 2 * s - 1; j++)		&#123;			cout &lt;&lt; &quot;*&quot;;		&#125;		cout &lt;&lt; endl;		s++;	&#125;&#125;



给出指定范围，找出其中的素数
# include&lt;iostream&gt;using namespace std;int main()&#123;    int a = 0, b = 0, d = 0;	cin &gt;&gt; a &gt;&gt; b;	for ( a +=1; a &lt; b; a++)	&#123;		for (int c = 2; c &lt; a; c++)		&#123;			if (a%c==0)			&#123;				d=1;				continue;			&#125;		&#125;		if (d == 0)	cout &lt;&lt; a &lt;&lt; endl;		else d = 0;	&#125;    return 0;&#125;

用1，2，3，4组合成没有相同数字的三位数
# include&lt;iostream&gt;using namespace std;int main()&#123;    	for (int  i = 1; i &lt;=4; i++)	&#123;		for (int  j = 1; j &lt;=4; j++)		&#123;			for (int k = 1; k &lt;= 4; k++)			&#123;				if (i != j &amp;&amp; j != k &amp;&amp; i != k)				&#123;					cout &lt;&lt; i * 100 + j * 10 + k &lt;&lt; endl;				&#125;			&#125;		&#125;	&#125;    return 0;&#125;


输入一个数字，输出它前面的所有素数
# include&lt;iostream&gt;using namespace std;int main()&#123;    int a = 1, b = 0, c = 0;	cout &lt;&lt; &quot;请输入大于2的数字&quot; &lt;&lt; endl;	cin &gt;&gt; b;	while (a&lt;b+1)	&#123;		for (int i = 2; i &lt; a; i++)		&#123;			if (a%i==0)			&#123;				c = 1;			&#125;		&#125;		if (c==0)		&#123;			cout &lt;&lt; a &lt;&lt; endl;		&#125;		else		&#123;			c = 0;		&#125;		a++;	&#125;    return 0;&#125;
